section.programlisting:
use Modern::Perl;
require Data2any; 

my $data = 
[[{ DOCTYPE => { root => 'html'}}
 ,{ html =>
    [{ body => 
       [{ 'h1 id=hw class=helloWorld' => 'Hello World Example'}
       ,{ p => 'Hello World, this is <b>real</b> easy! Wrote this'
             . ' on $date at $time!'
        }
       ]
     }
    ]
  }
 ]
];

my $d2xml = Data2any->new 
   ( inputData => $data
   , dataLabel => 'internal'
   , logging => 1
   , requestDocument => 0
   ); 

$d2xml->nodetreeFromData; 
$d2xml->xmlFromNodetree; 
$d2xml->postprocess;

# Importing modules
use Modern::Perl;
require Data2any;

# Creating the data structure
my $data = [[...][...]];

# Initialize module with the data
my $d2xml = Data2any->new( inputData => $data
                         , dataLabel => 'internal'
                         , logging => 1
                         , requestDocument => 0
                         );

# Convert to XML
$d2xml->nodetreeFromData;
$d2xml->xmlFromNodetree;        
$d2xml->postprocess;
